package org.cardanofoundation.hydrapoc;

import co.nstant.in.cbor.CborException;
import com.bloxbean.cardano.client.common.cbor.CborSerializationUtil;
import com.bloxbean.cardano.client.exception.CborDeserializationException;
import com.bloxbean.cardano.client.exception.CborSerializationException;
import com.bloxbean.cardano.client.transaction.spec.BigIntPlutusData;
import com.bloxbean.cardano.client.transaction.spec.Transaction;
import com.bloxbean.cardano.client.util.HexUtil;

import java.math.BigInteger;

public class TransactionDeserialisationCheck {

    public static void main(String[] args) throws CborDeserializationException, CborSerializationException, CborException {
        var tx = Transaction.deserialize(HexUtil.decodeHexString(""));

        System.out.println("Inputs:");
        System.out.println(tx.getBody().getInputs());

        System.out.println("Outputs:");
        System.out.println(tx.getBody().getOutputs());


        System.out.println(HexUtil.encodeHexString(CborSerializationUtil.serialize(new BigIntPlutusData(BigInteger.valueOf(221416136L)).serialize())));
    }

}
